function errorHandler(e){fetch("",{method:"POST",body:JSON.stringify({error:e,url:window.location.href})})}function scrollHandler(){const aos_elements=document.querySelectorAll("[aos]");aos_elements.forEach(element=>{if(element.getBoundingClientRect().top<window.innerHeight-150){const style=element.getAttribute("aos-style");const duration=element.getAttribute("aos-dur");if(style,duration){element.setAttribute("aos","active");element.style.setProperty("animation",`${ style } ${ duration } ease`)}}else{element.style.removeProperty("animation");element.setAttribute("aos","")}})}window.addEventListener("scroll",scrollHandler);
