class DuckDuckGo():
    def search(QUERY : str):
        res = []
        for i in BeautifulSoup(urlopen(f"https://lite.duckduckgo.com/lite?q={quote(QUERY)}").read().decode(), "html.parser").findAll("a", attrs={"rel" : "nofollow", "class" : "result-link"}):res.append(dict(title = i.text, url = unquote(URLSearchParams(i.get("href")).get("uddg"))))
        return dumps(res)

