function CookiesToJSON() {
  let cookies = document.cookie.split(";")
  let res = new Object()
  for (let i = 0; i < cookies.length; i ++) {
      let chunk = cookies[i].split("=")
      res[chunk[0]] = chunk[1]
  }
  return res
}
